// Helper function to shorten long URLs and text
function shortenText(text: string, maxLength: number = 30): string {
  if (!text) return '';

  // If it's a URL, show just the domain
  if (text.startsWith('http')) {
    try {
      const url = new URL(text);
      const hostname = url.hostname.replace('www.', '');
      return `${hostname}`;
    } catch {
      return text.substring(0, maxLength) + '...';
    }
  }

  if (text.length <= maxLength) return text;
  return text.substring(0, maxLength) + '...';
}

// Helper function to extract and render URLs as hyperlinks
function renderVillageNameWithLink(text: string) {
  if (!text) return 'N/A';

  // Check if text contains a URL
  const urlMatch = text.match(/(https?:\/\/[^\s]+)/);

  if (urlMatch) {
    const url = urlMatch[1];
    const beforeUrl = text.substring(0, text.indexOf(url));
    const afterUrl = text.substring(text.indexOf(url) + url.length);

    return (
      <>
        {beforeUrl}
        <a 
          href={url} 
          target="_blank" 
          rel="noopener noreferrer"
          className="text-blue-600 hover:underline break-all"
          title={url}
        >
          {shortenText(url)}
        </a>
        {afterUrl}
      </>
    );
  }

  return text;
}

